import React, { useEffect, useState } from "react";
import "./App.css";
import { ConnectButton, Modal } from "web3uikit";
import logo from "./images/Moralis.png";
import Coin from "./components/Coin"
import { modalGlobalConfig } from "antd/lib/modal/confirm";
import { abouts } from "./about";
import { useMoralisWeb3Api } from "react-moralis";

const App = () => {


  const [btc, setBtc] = useState(50);
  const [eth, setEth] = useState(50);
  const [ada, setAda] = useState(50);
  const [matic, setMatic] = useState(50);
  const [modalPrice, setModalPrice] = useState();
  const Web3Api = useMoralisWeb3Api();
  const [visible, setVisible] = useState(false);
  const [modalToken, setModalToken] = useState();

  return (
    <>
      <div className="header">
        <div className="logo">
          <img src={logo} alt="logo" height="50px" />
          Market Sentiment
        </div>
        <ConnectButton />
      </div>

      <div className="instructions">
        Analyse.Predict.Vote <br></br>
        Pick a Trend for your favourite coin
      </div>
      <div className="list">
        <Coin
          perc={btc}
          setPerc={setBtc}
          token={"BTC"}
          setModalToken={setModalToken}
          setVisible={setVisible} />
        <Coin
          perc={eth}
          setPerc={setEth}
          token={"ETH"}
          setModalToken={setModalToken}
          setVisible={setVisible} />
        <Coin
          perc={ada}
          setPerc={setAda}
          token={"ADA"}
          setModalToken={setModalToken}
          setVisible={setVisible} />
        <Coin
          perc={matic}
          setPerc={setMatic}
          token={"MATIC"}
          setModalToken={setModalToken}
          setVisible={setVisible} />
      </div>

      <Modal
        isVisible={visible}
        onCloseButtonPressed={() => setVisible(false)}
        hasFooter={false}
        title={modalToken}>
          <div>
            <span style={{color: "gold"}}>{`About`}</span>
          </div>
          <div>
            {modalToken && 
            abouts[abouts.findIndex((x) => x.token === modalToken)].about}
          </div>

      </Modal>

    </>


  );
};

export default App;
